# This script is used to calculate perplexity and non-greedy ratio 
# of texts generated by RL model's greedy decoding using the base model.

#!/bin/bash
current_file_path=$(dirname "$(realpath "$0")")
parent_dir=$(dirname "$current_file_path")
echo "Parent directory: $parent_dir"
export PYTHONPATH="$parent_dir:$PYTHONPATH"

python analyze_non_greedy.py \
    --base_model_path $parent_dir/models/DAPO-step-0 \
    --greedy_json $current_file_path/trained_greedy/greedy_decode_results.json